import Display from './Display';
import ButtonContainer from './ButtonContainer';
import { useState } from 'react';
import './App.css';

function App() {

  const [text, setText] = useState('123');

  const formEquation = (currentEq, mathToAppend) => {
    let tempEq = text;
    setText(tempEq.concat(mathToAppend));

  }

  const handleClick = buttonName => {
    if (buttonName == '=') {
      console.log("EQUALS!")
    } else {
      formEquation(text, buttonName);
      console.log("COMPUTE!")
    }
    setText(buttonName);
  };

  return (
    <div className='calc-container'>
      <Display text={text}/>
      <ButtonContainer clickHandler={handleClick}/>
    </div>
  );
}

export default App;
