  const formEquation = (currentEq, mathToAppend) => {
    let tempEq = text;
    if (text === 0) {
      setText(mathToAppend);
    } else { 
      setText(tempEq.concat(mathToAppend));
      console.log(text);
    }
  }

  const handleClick = buttonName => {
    if (buttonName === '=') {
      setText(parseInt(evil(text)));
    } else if (buttonName === 'C'){
      setText('0');
    } else {
      formEquation(text, buttonName);
